def zeroStuff(modeIn):
	global ticks, directionUp, currentData
	ticks = 0
	directionUp = True
	updateTime(None)
	setTimerOff()
	if modeIn == MODE_NORMAL:
		currentData = defaults()
		pickleIt(currentData[FILENAME], currentData)
	updateTime(None)


def startTimer():
	global timerRunning, currentData
	timerRunning = True
	setTimerUp()
	updateTime(True)


def stopTimer(stopMode):
	global timerRunning
	timerRunning = False
	setTimerOff()
	updateTime(None)


